import React,{useState} from "react";


const Main = () =>{


    const[musteriler,setMusteriler] = useState({"isim":"bos"})

    const musterileriGuncelle =()=>{

        setMusteriler({
            "isim":"ahmet",
            "soyşsim":"demir",
            "uyelik durumu":"aktif"
        })
    }


    return(
        <div>

            <p> {musteriler.isim} </p>
            <button onClick={()=>musterileriGuncelle()}>Degeri değiştir</button>

        </div>
    );
}

export default Main;
