import React from 'react'
import { Form, FormGroup, Label, Input,ButtonGroup,Button } from 'reactstrap';
import 'alertifyjs/build/css/alertify.css';
import alertify from "alertifyjs"
import 'bootstrap/dist/css/bootstrap.min.css';



const UpdateEmploye = (props) => {


    const nameChange = (event) => {

        props.sAd(event.target.value);
        console.log(event.target.value);
    }
    const soyadChange = (event) => {

        props.sSoyad(event.target.value);
        console.log(event.target.value);
    }

    const dtChange = (event) => {

        props.sDt(event.target.value);
        console.log(event.target.value);
    }

    const udChange = (event) => {

        props.sUd(event.target.value);
        console.log(event.target.value);
    }


    const onSubmitHandler=(event)=>{


        event.preventDefault();


        fetch("http://localhost:3000/musteriler/" + props.uid,
            {
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                method: "PATCH",
                body: JSON.stringify({
                    "Ad": this.state.ad,
                    "Soyad": this.state.soyad,
                    "DogumT": this.state.dt,
                    "Durum": this.state.ud
                })
            })
            .then(function (res) { console.log(res) })
            .catch(function (res) { console.log(res) })

        alertify.warning("Kayıtlar Başarıyla güncellendi")



    }


    return (
        <div>


            <br />
            <h2>{}</h2>


            <Form onSubmit={onSubmitHandler}>
                <FormGroup>
                    <Label>İsim</Label>
                    <Input onChange={this.onChangeHandler} type="text" name="ad" id="ad" />
                    <Label>Soy İsim</Label>
                    <Input onChange={this.onChangeHandler} type="text" name="soyad" id="soyad" />
                    <Label>Doğum Tarihi</Label>
                    <Input onChange={this.onChangeHandler} type="text" name="dt" id="dt" />
                    <Label>Üyelik Durumu</Label>
                    <Input onChange={this.onChangeHandler} type="select" name="ud" id="ud">
                        <option>Seçiniz</option>
                        <option >Aktif</option>
                        <option >Pasif</option>
                    </Input>
                </FormGroup>
                <ButtonGroup>
                    <Button color="warning" onClick={() => this.setState({ guncelle: false })} >İptal</Button>
                    <Input type="submit" className={"btn btn-success"} value="Güncelle" />
                </ButtonGroup>
            </Form>


        </div>
    )
}

export default UpdateEmploye;
